import React, { useState } from "react";
import "./HeartForm.css"; // We will create this simple CSS file below

const HeartForm = () => {
  const [formData, setFormData] = useState({
    age: 50, sex: 1, cp: 0, trestbps: 120, chol: 200,
    fbs: 0, restecg: 0, thalach: 150, exang: 0,
    oldpeak: 1.0, slope: 1, ca: 0, thal: 2
  });

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch("http://127.0.0.1:8000/api/predict/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Error:", error);
      alert("Prediction failed. Check backend connection.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h2>❤️ Heart Disease Risk Predictor</h2>

      <form onSubmit={handleSubmit} className="grid-form">
        {/* Helper function to generate inputs quickly */}
        {Object.keys(formData).map((key) => (
          <div key={key} className="input-group">
            <label>{key.toUpperCase()}</label>
            <input
              type="number"
              step="any"
              name={key}
              value={formData[key]}
              onChange={handleChange}
              required
            />
          </div>
        ))}

        <button type="submit" disabled={loading} className="submit-btn">
          {loading ? "Analyzing..." : "Predict Risk"}
        </button>
      </form>

      {result && (
        <div className={`result-box ${result.risk_level.toLowerCase()}`}>
          <h3>Risk Level: {result.risk_level}</h3>
          <p>Probability: {result.risk_percentage}%</p>
        </div>
      )}
    </div>
  );
};

export default HeartForm;
