import React, { useState } from 'react';
import axios from 'axios';
import './HeartForm.css';

const API_BASE_URL = "http://127.0.0.1:8000/api";

const fields = [
    { name: "age", label: "Age", type: "number", placeholder: "e.g. 45" },

    { name: "sex", label: "Sex", type: "select", options: [
        { v: 1, l: "Male" }, { v: 0, l: "Female" }
    ]},

    {
        name: "cp", label: "Chest Pain Type", type: "select", options: [
            { v: 0, l: "Typical Angina" },
            { v: 1, l: "Atypical Angina" },
            { v: 2, l: "Non-anginal Pain" },
            { v: 3, l: "Asymptomatic" }
        ]
    },

    { name: "trestbps", label: "Resting Blood Pressure", type: "number" },
    { name: "chol", label: "Serum Cholesterol", type: "number" },

    { name: "fbs", label: "Fasting Blood Sugar > 120", type: "select", options: [
        { v: 1, l: "True" }, { v: 0, l: "False" }
    ]},

    {
        name: "restecg", label: "Rest ECG", type: "select", options: [
            { v: 0, l: "Normal" },
            { v: 1, l: "ST-T Abnormal" },
            { v: 2, l: "LV Hypertrophy" }
        ]
    },

    { name: "thalach", label: "Max Heart Rate", type: "number" },

    { name: "exang", label: "Exercise Angina", type: "select", options: [
        { v: 1, l: "Yes" }, { v: 0, l: "No" }
    ]},

    { name: "oldpeak", label: "ST Depression", type: "number", step: "0.1" },

    {
        name: "slope", label: "Slope", type: "select", options: [
            { v: 0, l: "Upsloping" },
            { v: 1, l: "Flat" },
            { v: 2, l: "Downsloping" }
        ]
    },

    { name: "ca", label: "Major Vessels", type: "select", options: [
        { v: 0, l: "0" }, { v: 1, l: "1" }, { v: 2, l: "2" }, { v: 3, l: "3" }
    ]},

    {
        name: "thal", label: "Thal", type: "select", options: [
            { v: 1, l: "Normal" },
            { v: 2, l: "Fixed Defect" },
            { v: 3, l: "Reversible Defect" }
        ]
    }
];

export default function HeartForm() {

    const [form, setForm] = useState(
        fields.reduce((acc, f) => ({
            ...acc,
            [f.name]: f.type === "select" ? f.options[0].v : ""
        }), {})
    );

    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    // ✅ FIXED — converts strings to numbers
    const handleChange = (e) => {
        const { name, value } = e.target;

        const numeric = value === "" ? "" : Number(value);

        setForm(prev => ({
            ...prev,
            [name]: numeric
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        setLoading(true);
        setError(null);
        setResult(null);

        try {
            const response = await axios.post(
                `${API_BASE_URL}/predict/`,
                form,
                { headers: { "Content-Type": "application/json" } }
            );

            setResult(response.data);

        } catch (err) {
            // ✅ show real backend error
            setError(JSON.stringify(err.response?.data || "Server error", null, 2));
        } finally {
            setLoading(false);
        }
    };

    const handleRetrain = async () => {
        setLoading(true);

        try {
            await axios.post(`${API_BASE_URL}/retrain/`);
            alert("✅ Model retrained successfully");

        } catch {
            alert("❌ Retrain failed");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="glass-card">

            <form onSubmit={handleSubmit} className="prediction-form">

                <div className="form-grid">
                    {fields.map((f) => (
                        <div key={f.name} className="form-group">

                            <label>{f.label}</label>

                            {f.type === "select" ? (
                                <select name={f.name} value={form[f.name]} onChange={handleChange}>
                                    {f.options.map(o => (
                                        <option key={o.v} value={o.v}>{o.l}</option>
                                    ))}
                                </select>
                            ) : (
                                <input
                                    type={f.type}
                                    name={f.name}
                                    value={form[f.name]}
                                    step={f.step || 1}
                                    placeholder={f.placeholder}
                                    onChange={handleChange}
                                    required
                                />
                            )}

                        </div>
                    ))}
                </div>

                <div className="button-group">
                    <button disabled={loading} className="btn-predict">
                        {loading ? "Analysing..." : "Predict Health Risk"}
                    </button>

                    <button type="button" onClick={handleRetrain} disabled={loading} className="btn-retrain">
                        Retrain Model
                    </button>
                </div>
            </form>

            {result && (
                <div className={`result-container ${result.risk_level.toLowerCase()}`}>
                    <h3>Analysis Result</h3>
                    <h1>{result.risk_percentage}%</h1>
                    <p>Risk Level: <b>{result.risk_level}</b></p>
                </div>
            )}

            {error && <pre className="error-msg">{error}</pre>}

        </div>
    );
}
