import React, { useState } from 'react';
import axios from 'axios';
import './HeartForm.css';

const API_BASE_URL = "http://127.0.0.1:8000/api";

const fields = [
    { name: "age", label: "Age", type: "number", placeholder: "e.g. 45" },
    { name: "sex", label: "Sex", type: "select", options: [{ v: 1, l: "Male" }, { v: 0, l: "Female" }] },
    {
        name: "cp", label: "Chest Pain Type", type: "select", options: [
            { v: 0, l: "Typical Angina" }, { v: 1, l: "Atypical Angina" },
            { v: 2, l: "Non-anginal Pain" }, { v: 3, l: "Asymptomatic" }
        ]
    },
    { name: "trestbps", label: "Resting Blood Pressure", type: "number", placeholder: "mm Hg" },
    { name: "chol", label: "Serum Cholestoral", type: "number", placeholder: "mg/dl" },
    { name: "fbs", label: "Fasting Blood Sugar > 120 mg/dl", type: "select", options: [{ v: 1, l: "True" }, { v: 0, l: "False" }] },
    {
        name: "restecg", label: "Resting Electrocardiographic Results", type: "select", options: [
            { v: 0, l: "Normal" }, { v: 1, l: "ST-T Wave Abnormality" }, { v: 2, l: "Left Ventricular Hypertrophy" }
        ]
    },
    { name: "thalach", label: "Max Heart Rate Achieved", type: "number", placeholder: "bpm" },
    { name: "exang", label: "Exercise Induced Angina", type: "select", options: [{ v: 1, l: "Yes" }, { v: 0, l: "No" }] },
    { name: "oldpeak", label: "ST Depression Induced by Exercise", type: "number", step: "0.1", placeholder: "e.g. 1.5" },
    {
        name: "slope", label: "Slope of the Peak Exercise ST Segment", type: "select", options: [
            { v: 0, l: "Upsloping" }, { v: 1, l: "Flat" }, { v: 2, l: "Downsloping" }
        ]
    },
    { name: "ca", label: "Number of Major Vessels", type: "select", options: [{ v: 0, l: "0" }, { v: 1, l: "1" }, { v: 2, l: "2" }, { v: 3, l: "3" }, { v: 4, l: "4" }] },
    {
        name: "thal", label: "Thal", type: "select", options: [
            { v: 0, l: "Normal" }, { v: 1, l: "Fixed Defect" }, { v: 2, l: "Reversable Defect" }
        ]
    },
];

export default function HeartForm() {
    const [form, setForm] = useState(
        fields.reduce((acc, field) => ({ ...acc, [field.name]: field.type === "select" ? field.options[0].v : "" }), {})
    );
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setResult(null);
        setError(null);

        try {
            const response = await axios.post(`${API_BASE_URL}/predict/`, form);
            setResult(response.data);
        } catch (err) {
            setError(err.response?.data?.error || "Failed to get prediction. Ensure backend is running.");
        } finally {
            setLoading(false);
        }
    };

    const handleRetrain = async () => {
        setLoading(true);
        try {
            await axios.post(`${API_BASE_URL}/retrain/`);
            alert("Model retrained successfully with current data!");
        } catch (err) {
            alert("Retraining failed.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="glass-card">
            <form onSubmit={handleSubmit} className="prediction-form">
                <div className="form-grid">
                    {fields.map((f) => (
                        <div key={f.name} className="form-group">
                            <label>{f.label}</label>
                            {f.type === "select" ? (
                                <select name={f.name} onChange={handleChange} value={form[f.name]}>
                                    {f.options.map(opt => <option key={opt.v} value={opt.v}>{opt.l}</option>)}
                                </select>
                            ) : (
                                <input
                                    type={f.type}
                                    name={f.name}
                                    step={f.step || "1"}
                                    placeholder={f.placeholder}
                                    onChange={handleChange}
                                    value={form[f.name]}
                                    required
                                />
                            )}
                        </div>
                    ))}
                </div>
                <div className="button-group">
                    <button type="submit" disabled={loading} className="btn-predict">
                        {loading ? "Analysing..." : "Predict Health Risk"}
                    </button>
                    <button type="button" onClick={handleRetrain} disabled={loading} className="btn-retrain">
                        Optimize AI Model
                    </button>
                </div>
            </form>

            {result && (
                <div className={`result-container ${result.risk_level.toLowerCase()}`}>
                    <h3>Analysis Result</h3>
                    <div className="risk-score">
                        <span className="percent">{result.risk_percentage}%</span>
                        <span className="label">Risk Probability</span>
                    </div>
                    <p className="risk-level">Risk Level: <strong>{result.risk_level}</strong></p>
                </div>
            )}

            {error && <div className="error-msg">{error}</div>}
        </div>
    );
}
